import React from 'react';
import { useState, useEffect } from 'react';
import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';
import { AppBar, Toolbar, TextField, Typography, Button, Select, MenuItem, FormGroup, FormControlLabel, Checkbox } from '@material-ui/core';
import Backdrop from '@material-ui/core/Backdrop';
import CircularProgress from '@material-ui/core/CircularProgress';
import axios from 'axios';
import * as moment from 'moment-timezone';

const useStyles = makeStyles((t: Theme) => createStyles({
	app: { },
	toolbar: { },
	title: { margin: "1em 1em 1em 0", flexGrow: 1 },
	content: { padding: "2em 0em 1em 0em", backgroundColor: "#F6F6F6" },
	execButton: { minWidth: "6em", minHeight: "3em" },
	barButton: { borderColor: t.palette.primary.contrastText, color: t.palette.primary.contrastText },
	backdrop: { zIndex: t.zIndex.drawer + 1, color: '#fff' },
	attrAreaLine: { display: "flex", alignItems: "center" },
	commandArea: { backgroundColor: "#FFFFFF", padding: "2.3em 1.5em 2em 1.5em", margin: "0 0em 2em 0em" },
	execButtonMini: { minWidth: "4em", minHeight: "2em" },
	bottomSpace1: { marginBottom: "1em" },
	bottomSpace1_5: { marginBottom: "1.5em" },
	bottomSpace2: { marginBottom: "2em" },
	bottomSpace3: { marginBottom: "3em" },
	leftSpace1: { marginLeft: "1em" },
	rightSpace1: { marginRight: "1em" }
}));

function App() {
	const c = useStyles();

	const [message, setMessage] = useState("");
	const [userName, setUserName] = useState<string | null>(null);
	const [userAttributeName, setUserAttributeName] = useState<string>("name.last");
	const [userAttributeValue, setUserAttributeValue] = useState<string | null>(null);
	const [userDetailImage, setUserDetailImage] = useState<string | null>(null);
	const [rawText, setRawText] = useState<string | null>("test string xyz & 日本語のテスト文字列");
	const [encryptedText, setEncryptedText] = useState<string | null>(null);
	const [logText, setLogText] = useState<string | null>("dummy log text & 日本語のダミー文字列");
	const [mailTitle, setMailTitle] = useState<string | null>("メールのテスト：タイトル");
	const [mailBody, setMailBody] = useState<string | null>("メールのテスト：本文");
	const [mailSessionLess, setMailSessionLess] = useState<boolean>(false);
	const [signTarget, setSignTarget] = useState<string | null>("sample string abc & 日本語のサンプル文字列");
	const [signResult, setSignResult] = useState<string | null>(null);
	const [exAttrKey, setExAttrKey] = useState<string | null>(null);
	const [exAttrValue, setExAttrValue] = useState<string | null>(null);
	//const [notificationBody, setNotificationBody] = useState<string>("");
	const [qrLoginEventId, setQrLoginEventId] = useState<string>("event_A");
	const [aggregateUserIds, setAggregateUserIds] = useState<string>("");
	const [aggregateHighAccuracy, setAggregateHighAccuracy] = useState<boolean>(true);
	const [isLoading, setLoading] = useState(false);

	const updateMessageText = (msg: string) => {
		setMessage(moment.tz("Asia/Tokyo").format("YYYY/MM/DD HH:mm:ss") + "(JST)：" + msg);
	};

	const getErrorMessageText = (e: any) => {
		return (d => d ? `${d} : ${e}` : `${e}`)(e?.response?.data?.error);
	}

	const loading = (execute: () => void, infinit?: boolean): (() => void) => {
		return async () => {
			setLoading(true);
			try {
				await new Promise(r => setTimeout(r, 200));
				await execute();
			}
			finally {
				if (!infinit) {
					setLoading(false);
				}
			}
		};
	};

	const onLogin = loading(async () => {
		try {
			const res = await axios.post("/api/authstart", {}, {});

			window.location.href = res.data.auth_url;
		}
		catch (err) {
			updateMessageText(getErrorMessageText(err));
		}
	}, true);

	const clearLocalData = () => {

		setUserName(null);
	};

	const onLogout = loading(async () => {
		var msg = null;

		try {
			await axios.post("/api/logout", {}, {});
		}
		catch (err) {
			msg = getErrorMessageText(err);
		}

		clearLocalData();

		updateMessageText(msg ? msg : "Logout completed.");
	});

	const apiErrorAction = (err: any) => {
		console.log(err);
		if (err.response?.status === 401) { // 401 Unauthorized
			clearLocalData();
			updateMessageText("ERROR : need to login again.");
		}
		else {
			updateMessageText(getErrorMessageText(err));
		}
	}

	const readAttribute = (attrName: string, settter: (v: string) => void): (() => void) => { return loading(async () => {
		try {
			const param = { attr: attrName };
			const res = await axios.post("/api/getUserAttribute", param, {});

			const value = res.data.result

			settter(value);
			updateMessageText(`SUCCESS : get ${attrName} -> ${value}`);
		}
		catch (err) {
			apiErrorAction(err);
		}
	})};

	const writeAttribute = (attrName: string, value: string | null, settter: (v: string) => void): (() => void) => { return loading(async () => {
		try {
			const param = { attr: attrName, value: value };
			const res = await axios.post("/api/setUserAttribute", param, {});

			const resultValue = res.data.result;
			settter(resultValue);

			updateMessageText(`SUCCESS : set ${attrName} -> ${value} -> ${resultValue}`);
		}
		catch (err) {
			apiErrorAction(err);
		}
	})};

	const onReadUserAttributeAt = loading(async () => {
		try {
			await readAttribute(userAttributeName || "", setUserAttributeValue)();
		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	const onWriteUserAttributeAt = loading(async () => {
		try {
			await writeAttribute(userAttributeName || "", userAttributeValue, setUserAttributeValue)();
		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	// const onUserImageRead = readAttribute("image.1", setUserDetailImage);

	// const onUploadFile = (inputEvent: any) => {
	// 	const reader = new FileReader();
	// 	reader.onload = async (e) => {
	// 		if (e.target) {
	// 			const image = e.target.result as string;
	// 			await writeAttribute("image.1", image, setUserDetailImage)();
	// 		}
	// 	};
	// 	reader.readAsDataURL(inputEvent.target.files[0]);
	// };

	// const onCreateSignature = loading(async () => {
	// 	try {
	// 		const param = { message: signTarget || "" };

	// 		const res = await axios.post("/api/createSignature", param, {});

	// 		const signature = res.data.signature;

	// 		if (!signature) {
	// 			throw new Error("FAILED : signature create");
	// 		}

	// 		setSignResult(signature);
	// 		updateMessageText("SUCCESS : signature create");
	// 	}
	// 	catch (err) {
	// 		apiErrorAction(err);
	// 	}
	// });

	// const onVerifySignature = loading(async () => {
	// 	try {
	// 		const param = {
	// 			message: signTarget || "",
	// 			signature: signResult || ""
	// 		};

	// 		const res = await axios.post("/api/verifySignature", param, {});

	// 		const result = res.data.result === "ok";

	// 		updateMessageText(result ? "signature check : OK" : "Invalid signature");
	// 	}
	// 	catch (err) {
	// 		apiErrorAction(err);
	// 	}
	// });

	const onSendLog = loading(async () => {
		try {
			const param = { log_text: logText || "" };
			const res = await axios.post("/api/writeLogText", param, {});

			updateMessageText(`SUCCESS : send log : ${res.data?.id}`);
		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	const onSendMail = loading(async () => {
		try {
			const param = mailSessionLess === true ? 
				{ title: mailTitle || "", body: mailBody || "", session_less: mailSessionLess } :
				{ title: mailTitle || "", body: mailBody || "" }

			const res = await axios.post("/api/sendMail", param, {});

			updateMessageText(`SUCCESS : send mail : ${res.data?.id}`);
		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	const onExAttrGet = loading(async () => {
		try {
			const param = { key: exAttrKey };
			const res = await axios.post("/api/getExAttr", param, {});

			setExAttrValue(res.data.result);
			updateMessageText(`SUCCESS : get ex attr : ${res.data.result}`);
		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	const onExAttrSet= loading(async () => {
		try {
			const param = { key: exAttrKey, value: exAttrValue };
			const res = await axios.post("/api/setExAttr", param, {});

			updateMessageText(`SUCCESS : set ex attr : ${res.data?.result}`);
		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	const onExAttrDelete = loading(async () => {
		try {
			const param = { key: exAttrKey };
			const res = await axios.post("/api/deleteExAttr", param, {});

			updateMessageText(`SUCCESS : delete ex attr : ${res.data?.result}`);
		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	//const onSendNotification = loading(async () => {
	//});

	const onSendQrLoginMail = loading(async () => {
		try {
			const param = { key: qrLoginEventId };
			const res = await axios.post("/api/sendQrLoginMail", param, {});

			updateMessageText(`SUCCESS : send qr login mail : ${res.data?.result}`);
		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	const onOpenQrLoginPage = () => {
		window.open("/qrlogin");
	};

	const onUserSetting = () => {

	};

	const onDebugPage = () => {
		window.open("/debug/notification/list");
	}

	const onAggregateUserInfo = loading(async () => {
		try {
			const param = { user_ids: aggregateUserIds };
			const res = await axios.post("/api/aggregateUserInfo", param, {});

			if (res.data?.result === true) {
				updateMessageText(`SUCCESS : aggregate user data`);

				const bom = new Uint8Array([0xEF, 0xBB, 0xBF]);
				const url = window.URL.createObjectURL(new Blob([bom, res.data?.csv], {type: "text/csv"}));
				const link = document.createElement('a');
				link.href = url;
				link.setAttribute('target', '_blank');
				link.setAttribute('download', (new Date()).toISOString() + ".csv" );

				document.body.appendChild(link);
				link.click();
				link.remove();
				URL.revokeObjectURL(url);
			}
			else {
				updateMessageText(`FAILED : aggregate user data`);
			}

		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	useEffect(() => {
		document.title = process.env.REACT_APP_APPLICATION_NAME_SHORT || "";

		loading(async () => {
			try {
				const res = await axios.post("/api/status", {}, {});

				if (res.data?.login === true) {
					setUserName(res.data?.name);
				}
			}
			catch (err) {
				apiErrorAction(err);
			}
		})();
	// eslint-disable-next-line react-hooks/exhaustive-deps
	}, []);

	return (
		<>
			<div className={c.app}>
				<AppBar position="static">
					<Toolbar className={c.toolbar}>
						<Typography variant="h6" className={c.title}>{process.env.REACT_APP_APPLICATION_NAME}</Typography>
						<Button variant="outlined" className={[c.execButton, c.barButton].join(" ")} onClick={onUserSetting}>User</Button>
						<Button variant="outlined" className={[c.execButton, c.barButton, c.leftSpace1].join(" ")} onClick={onDebugPage}>Debug</Button>
					</Toolbar>
				</AppBar>
				<div className={c.content}>
					<div className={c.commandArea}>
						<TextField id="message" label="message" fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: true}} variant="outlined" value={message}/>
					</div>
					<div className={[c.attrAreaLine, c.commandArea].join(" ")}>
						<TextField id="user_name" label="user name" style={{width: "26em"}} autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: true}} variant="outlined" value={userName || ""}/>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onLogin} disabled={userName !== null}>Login</Button>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onLogout}>Logout</Button>
					</div>
					<div className={[c.commandArea].join(" ")}>
						<TextField label="ユーザ属性" className={[c.bottomSpace1].join(" ")} fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: false}} variant="outlined" value={userAttributeValue || ""} onChange={e => setUserAttributeValue(e.target.value)}/>
						<Select style={{width: "12em"}} variant="outlined" value={userAttributeName} onChange={e => setUserAttributeName(String(e.target.value))}>
							<MenuItem value="name.last">姓</MenuItem>
							<MenuItem value="name.first">名</MenuItem>
							<MenuItem value="name.ruby.last">姓(カナ)</MenuItem>
							<MenuItem value="name.ruby.first">名(カナ)</MenuItem>
							<MenuItem value="mail_address">メールアドレス</MenuItem>
							<MenuItem value="gender">性別</MenuItem>
							<MenuItem value="birthday">生年月日</MenuItem>
							<MenuItem value="birthday.year">生年月日：年</MenuItem>
							<MenuItem value="birthday.month">生年月日：月</MenuItem>
							<MenuItem value="birthday.day">生年月日：日</MenuItem>
							<MenuItem value="tel_number">電話番号</MenuItem>
							<MenuItem value="address">住所</MenuItem>
							<MenuItem value="address.zipcode">住所：郵便番号</MenuItem>
							<MenuItem value="address.pref">住所：都道府県</MenuItem>
							<MenuItem value="address.city">住所：市区町村</MenuItem>
							<MenuItem value="address.town">住所：町域</MenuItem>
							<MenuItem value="address.detail">住所：丁目以降</MenuItem>
						</Select>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onReadUserAttributeAt}>read</Button>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onWriteUserAttributeAt}>write</Button>
					</div>
					<div className={[c.attrAreaLine, c.commandArea].join(" ")}>
						<div style={{width: "26em", maxHeight: "26em", display: "inline"}}>
							{userDetailImage !== null ? <img src={userDetailImage} alt="ユーザ画像" style={{maxWidth: "26em", maxHeight: "26em"}}/> : <span>ユーザ画像</span>}
						</div>

						<Button variant="contained" color="primary" component="label" className={[c.execButton, c.leftSpace1].join(" ")}>
							WRITE
							<input id="image_input_tag" accept="image/*" type="file" style={{display: "none"}} onChange={onUploadFile} onClick={(e: any) => e.target.value = ""}/>
						</Button>
					</div>
					<div className={[c.commandArea].join(" ")}>
						<TextField id="sign_target" label="target text" className={[c.bottomSpace1].join(" ")} fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: false}} variant="outlined" value={signTarget || ""} onChange={e => setSignTarget(e.target.value)}/>
						<TextField id="sign_result" label="signature" className={[c.bottomSpace1].join(" ")} fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: false}} variant="outlined" value={signResult || ""} onChange={e => setSignResult(e.target.value)}/>
						<Button variant="contained" color="primary" className={[c.execButton].join(" ")} onClick={onCreateSignature}>Create Sign</Button>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onVerifySignature}>Sign Check</Button>
					</div>
					<div className={[c.commandArea].join(" ")}>
						<TextField id="raw_data" label="raw text" className={[c.bottomSpace1].join(" ")} fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: false}} variant="outlined" value={rawText || ""} onChange={e => setRawText(e.target.value)}/>
						{/* <Button variant="contained" color="primary" className={[c.bottomSpace1_5, c.execButton].join(" ")} onClick={onEncrypt}>↓ Encrypt</Button>
						<Button variant="contained" color="primary" className={[c.bottomSpace1_5, c.execButton, c.leftSpace1].join(" ")} onClick={onDecrypt}>↑ Decrypt</Button> */}
						<TextField id="encrypted_text" label="encrypted text" fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: false}} variant="outlined" value={encryptedText || ""} onChange={e => setEncryptedText(e.target.value)}/>
					</div>
					<div className={[c.commandArea].join(" ")}>
						<TextField id="send_log" label="log" className={[c.bottomSpace1].join(" ")} fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: false}} variant="outlined" value={logText || ""} onChange={e => setLogText(e.target.value)}/>
						<Button variant="contained" color="primary" className={c.execButton} onClick={onSendLog}>Send Log</Button>
					</div>
					<div className={[c.commandArea].join(" ")}>
						<TextField id="send_mail_title" label="title" className={[c.bottomSpace1].join(" ")} fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: false}} variant="outlined" value={mailTitle || ""} onChange={e => setMailTitle(e.target.value)}/>
						<TextField id="send_mail_body" label="body" className={[c.bottomSpace1].join(" ")} fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: false}} variant="outlined" value={mailBody || ""} onChange={e => setMailBody(e.target.value)} multiline rows={3}/>
						<FormGroup>
							<FormControlLabel control={<Checkbox checked={mailSessionLess} onChange={e => setMailSessionLess(e.target.checked)} />} label="セッションなしで送信" />
						</FormGroup>
						<Button variant="contained" color="primary" className={c.execButton} onClick={onSendMail}>Send Mail</Button>
					</div>
					<div className={[c.commandArea].join(" ")}>
						<TextField id="ex_attr_key" label="key" className={[c.bottomSpace1].join(" ")} fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: false}} variant="outlined" value={exAttrKey || ""} onChange={e => setExAttrKey(e.target.value)}/>
						<TextField id="ex_attr_value" label="value" className={[c.bottomSpace1].join(" ")} fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: false}} variant="outlined" value={exAttrValue || ""} onChange={e => setExAttrValue(e.target.value)}/>
						<Button variant="contained" color="primary" className={[c.execButton].join(" ")} onClick={onExAttrGet}>Ex-Attr Get</Button>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onExAttrSet}>Set</Button>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onExAttrDelete}>Delete</Button>
					</div>

					<div className={[c.commandArea].join(" ")}>
						<Select style={{width: "12em"}} variant="outlined" value={qrLoginEventId} onChange={e => setQrLoginEventId(String(e.target.value))}>
							<MenuItem value="event_A">テストイベントA</MenuItem>
							<MenuItem value="event_B">テストイベントB</MenuItem>
							<MenuItem value="event_C">テストイベントC</MenuItem>
						</Select>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onSendQrLoginMail}>QRログインメール送信</Button>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onOpenQrLoginPage}>QRログイン画面を開く</Button>
					</div>
					<div className={[c.commandArea].join(" ")}>
						<TextField id="aggregate" label="user ids" className={[c.bottomSpace1].join(" ")} fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: false}} variant="outlined" value={aggregateUserIds || ""} onChange={e => setAggregateUserIds(e.target.value)} multiline rows={3}/>
						<FormGroup>
							<FormControlLabel control={<Checkbox checked={aggregateHighAccuracy} onChange={e => setAggregateHighAccuracy(e.target.checked)} />} label="高い精度で集計" />
						</FormGroup>
						<Button variant="contained" color="primary" className={c.execButton} onClick={onAggregateUserInfo}>ユーザ情報集計(chrome only)</Button>
					</div>
				</div>
			</div>
			<Backdrop className={c.backdrop} open={isLoading}>
				<CircularProgress color="inherit" />
			</Backdrop>
		</>
	);
}

export default App;
