import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';
import { AppBar, Toolbar, TextField, Typography, Button, Select, MenuItem, FormGroup, FormControlLabel, Checkbox } from '@material-ui/core';
import Backdrop from '@material-ui/core/Backdrop';
import CircularProgress from '@material-ui/core/CircularProgress';
import axios from 'axios';
import * as moment from 'moment-timezone';

const useStyles = makeStyles((t: Theme) => createStyles({
	app: { },
	toolbar: { },
	title: { margin: "1em 1em 1em 0", flexGrow: 1 },
	content: { padding: "2em 0em 1em 0em", backgroundColor: "#F6F6F6" },
	execButton: { minWidth: "6em", minHeight: "3em" },
	barButton: { borderColor: t.palette.primary.contrastText, color: t.palette.primary.contrastText },
	backdrop: { zIndex: t.zIndex.drawer + 1, color: '#fff' },
	attrAreaLine: { display: "flex", alignItems: "center" },
	commandArea: { backgroundColor: "#FFFFFF", padding: "2.3em 1.5em 2em 1.5em", margin: "0 0em 2em 0em" },
	execButtonMini: { minWidth: "4em", minHeight: "2em" },
	bottomSpace1: { marginBottom: "1em" },
	bottomSpace1_5: { marginBottom: "1.5em" },
	bottomSpace2: { marginBottom: "2em" },
	bottomSpace3: { marginBottom: "3em" },
	leftSpace1: { marginLeft: "2em" },
	rightSpace1: { marginRight: "2em" }
}));

function App() {
	const c = useStyles();

	const [message, setMessage] = useState("");
	const [userName, setUserName] = useState<string | null>(null);
	const [userAttributeName, setUserAttributeName] = useState<string>("name.last");
	const [userAttributeValue, setUserAttributeValue] = useState<string | null>(null);
	const [userDetailImage, setUserDetailImage] = useState<string | null>(null);
	const [rawText, setRawText] = useState<string | null>("test string xyz & 日本語のテスト文字列");
	const [encryptedText, setEncryptedText] = useState<string | null>(null);
	const [logText, setLogText] = useState<string | null>("dummy log text & 日本語のダミー文字列");
	const [mailSessionLess, setMailSessionLess] = useState<boolean>(false);
	const [signTarget, setSignTarget] = useState<string | null>("sample string abc & 日本語のサンプル文字列");
	const [signResult, setSignResult] = useState<string | null>(null);
	const [exAttrKey, setExAttrKey] = useState<string | null>(null);
	const [exAttrValue, setExAttrValue] = useState<string | null>(null);
	//const [notificationBody, setNotificationBody] = useState<string>("");
	const [qrLoginEventId, setQrLoginEventId] = useState<string>("event_A");
	const [aggregateHighAccuracy, setAggregateHighAccuracy] = useState<boolean>(true);
	const [isLoading, setLoading] = useState(false);

	const getErrorMessageText = (e: any) => {
		return (d => d ? `${d} : ${e}` : `${e}`)(e?.response?.data?.error);
	}

	const loading = (execute: () => void, infinit?: boolean): (() => void) => {
		return async () => {
			setLoading(true);
			try {
				await new Promise(r => setTimeout(r, 200));
				await execute();
			}
			finally {
				if (!infinit) {
					setLoading(false);
				}
			}
		};
	};

	const onLogin = loading(async () => {
		try {
			const res = await axios.post("/api/authstart", {}, {});

			window.location.href = res.data.auth_url;
		}
		catch (err) {
			// updateMessageText(getErrorMessageText(err));
		}
	}, true);

	const clearLocalData = () => {

		setUserName(null);
	};

	const onLogout = loading(async () => {
		var msg = null;

		try {
			await axios.post("/api/logout", {}, {});
		}
		catch (err) {
			// msg = getErrorMessageText(err);
		}

		clearLocalData();

		// updateMessageText(msg ? msg : "Logout completed.");
	});

	const apiErrorAction = (err: any) => {
		console.log(err);
		if (err.response?.status === 401) { // 401 Unauthorized
			clearLocalData();
			// updateMessageText("ERROR : need to login again.");
		}
		else {
			// updateMessageText(getErrorMessageText(err));
		}
	}

	const readAttribute = (attrName: string, settter: (v: string) => void): (() => void) => { return loading(async () => {
		try {
			const param = { attr: attrName };
			const res = await axios.post("/api/getUserAttribute", param, {});

			const value = res.data.result

			settter(value);
			// updateMessageText(`SUCCESS : get ${attrName} -> ${value}`);
		}
		catch (err) {
			apiErrorAction(err);
		}
	})};

	const writeAttribute = (attrName: string, value: string | null, settter: (v: string) => void): (() => void) => { return loading(async () => {
		try {
			const param = { attr: attrName, value: value };
			const res = await axios.post("/api/setUserAttribute", param, {});

			const resultValue = res.data.result;
			settter(resultValue);

			// updateMessageText(`SUCCESS : set ${attrName} -> ${value} -> ${resultValue}`);
		}
		catch (err) {
			apiErrorAction(err);
		}
	})};

	const onReadUserAttributeAt = loading(async () => {
		try {
			await readAttribute(userAttributeName || "", setUserAttributeValue)();
		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	const onWriteUserAttributeAt = loading(async () => {
		try {
			await writeAttribute(userAttributeName || "", userAttributeValue, setUserAttributeValue)();
		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	const onCreateSignature = loading(async () => {
		try {
			const param = { message: signTarget || "" };

			const res = await axios.post("/api/createSignature", param, {});

			const signature = res.data.signature;

			if (!signature) {
				throw new Error("FAILED : signature create");
			}

			setSignResult(signature);
			// updateMessageText("SUCCESS : signature create");
		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	const onSendQrLoginMail = loading(async () => {
		try {
			const param = { key: qrLoginEventId };
			const res = await axios.post("/api/sendQrLoginMail", param, {});

			// updateMessageText(`SUCCESS : send qr login mail : ${res.data?.result}`);
		}
		catch (err) {
			apiErrorAction(err);
		}
	});

	const onOpenQrLoginPage = () => {
		window.open("/qrlogin");
	};

	const onUserSetting = () => {

	};

	const onDebugPage = () => {
		window.open("/debug/notification/list");
	}

	useEffect(() => {
		document.title = process.env.REACT_APP_APPLICATION_NAME_SHORT || "";

		loading(async () => {
			try {
				const res = await axios.post("/api/status", {}, {});

				if (res.data?.login === true) {
					setUserName(res.data?.name);
				}
			}
			catch (err) {
				apiErrorAction(err);
			}
		})();
	// eslint-disable-next-line react-hooks/exhaustive-deps
	}, []);

	return (
		<>
			<div className={c.app}>
				<AppBar position="static">
					<Toolbar className={c.toolbar}>
						<Typography variant="h6" className={c.title}>{process.env.REACT_APP_APPLICATION_NAME}</Typography>
					</Toolbar>
				</AppBar>
				<div className={c.content}>
					<div className={[c.attrAreaLine, c.commandArea].join(" ")}>
						<TextField id="user_name" label="user name" style={{width: "26em"}} autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: true}} variant="outlined" value={userName || ""}/>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onLogin} disabled={userName !== null}>Login</Button>
					</div>
					<div className={[c.attrAreaLine, c.commandArea].join(" ")}>
						<TextField className={[c.bottomSpace1].join(" ")} fullWidth autoComplete="off" InputLabelProps={{shrink: true}} InputProps={{readOnly: false}} variant="outlined" value={userAttributeValue || ""} onChange={e => setUserAttributeValue("temperature")}/>
						<h1 color="primary" >℃</h1>
					</div>
					<div className={[c.commandArea].join(" ")}>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onWriteUserAttributeAt}>体温記録</Button>
					</div>
					<div className={[c.commandArea].join(" ")}>
						<Select style={{width: "12em"}} variant="outlined" value={qrLoginEventId} onChange={e => setQrLoginEventId(String(e.target.value))}>
							<MenuItem value="event_A">テストイベントA</MenuItem>
							<MenuItem value="event_B">テストイベントB</MenuItem>
							<MenuItem value="event_C">テストイベントC</MenuItem>
						</Select>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onSendQrLoginMail}>QRログインメール送信</Button>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onOpenQrLoginPage}>QRログイン画面を開く</Button>
					</div>

					<div className={[c.attrAreaLine, c.commandArea].join(" ")}>
						<Button variant="contained" color="primary" className={[c.execButton, c.leftSpace1].join(" ")} onClick={onLogout}>Logout</Button>
					</div>

				</div>
			</div>
			<Backdrop className={c.backdrop} open={isLoading}>
				<CircularProgress color="inherit" />
			</Backdrop>
		</>
	);
}

export default App;
